export default function runOnImageLoad(className, callback) {
  var isLoading = true;

  while (isLoading) {
    var loading = [].slice.call(document.querySelectorAll(className)).filter(function (img) {
      return img.complete !== true;
    });

    if (!loading.length > 0) {
      isLoading = true;
      callback();
      return;
    }
  }
}
